// Props:
// * name: Skill name / technology
// * 

const expBars = (level) => {
    var bars = []
    for (var i=0; i<level; i++){
        bars.push(<span key={i} className="min-w-4 h-5 border-2 dark:bg-green-600 dark:border-green-700 mx-0.5">&nbsp;</span>)
    }

    for (i=5; i>level; i--){
        bars.push(<span key={i} className="min-w-4 h-5 border-2 dark:border-green-700 mx-0.5">&nbsp;</span>)
    }
    return bars;
}

const SkillCard = (props)  => {
    return (
        <div className="text-center shadow-lg p-5 rounded-xl my-5 dark:bg-neutral-600 w-2/4 mr-4">
            <div className="mb-4"><b>{props.name}</b></div>
            <span className="flex"> <p className="mr-3" >Experience: </p> {expBars(4)} </span>
            <span className="flex"> <p className="mr-3" >Knowledge: </p> {expBars(4)} </span>
        </div>
    )
}

export default SkillCard;
